import { View, Text, Modal, StyleSheet, Pressable, Image, ImageBackground, Dimensions} from 'react-native'

import SagradaModal from "../Modals/SagradaModal"
import BatlloPedreraModal from "../Modals/BatlloPedreraModal"
import ParcGuellModal from "../Modals/ParcGuellModal"

const width = Dimensions.get("window").width
const height = Dimensions.get("window").height

const GaudiItem = ({ id, item }) => {
  return (
    <>
      <ImageBackground
            style={{borderRadius: 40}}
            source={{uri:("https://99percentinvisible.org/app/uploads/2019/05/interior.jpg")}}
      >

            <SagradaModal key={id} item={item}  />
      </ImageBackground>

      <ImageBackground
            
            source={{uri:("https://i.pinimg.com/originals/47/85/c9/4785c9bb1cd040db7acb3afd0eb675ff.jpg")}}
      >   
            <ParcGuellModal key={id} />
      </ImageBackground>
      
      <ImageBackground
            
            source={{uri:("http://blog.mozaico.com/wp-content/uploads/2017/07/Depositphotos_4311323_l-2015.jpg")}}
      >
            <BatlloPedreraModal key={id} item={item}  />
      </ImageBackground>
    </>
  )
}

export default GaudiItem

const styles = StyleSheet.create({
    centeredView: {
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center",
        marginRight: 5,
        marginLeft: 5,
        marginTop: 20,
      },
      modalView: {
        justifyContent: "center",
        backgroundColor: "#fff",
        width: "100%",
        height: "95%",
        borderRadius: 15,
        padding: 20,
        marginTop: 15,
        elevation: 3.3
      },
      openView: {
        backgroundColor: "grey",
        
      },
      imageBackground: {
        opacity: 1,
        flex: 1,
        resizeMode: "contain",
        width: width,
        height: 310,
    },
       //   gaudi card
      gaudi: {
        borderRadius: 15,
        padding: 100,
        elevation: 2,
        marginRight: 0,
        backgroundColor: "white"
      },
      button: {
        borderRadius: 15,
        padding: 15,
        elevation: 2,
        marginTop: 100,
        marginHorizontal: 0,
        backgroundColor: "white"
      },
      buttonClose: {
        backgroundColor: "white",
        margin: 20
      },
      textStyle: {
        color: "black",
        fontWeight: "bold",
        textAlign: "center"
      },
      modalText: {
        marginBottom: 15,
        textAlign: "center"
      },
  })
