import { View, Text, StyleSheet, ImageBackground, Dimensions } from 'react-native'
import Swiper from 'react-native-swiper'
// https://www.npmjs.com/package/react-native-swiper
// npm i --save react-native-swiper@next


const { height, width } = Dimensions.get("window")

const InfoSlider= () => {
  return (
  <>
    <View style={styles.sliderContainer}>
      <Swiper 
          height={400}
          width={300}
          style={styles.wrapper} 
          showsButtons={false} 
          autoplay={true}
          autoplayTimeout={2}
          index={0}
          dotColor={"white"}
          activeDotColor={"#eb3d34"}
        >
    <ImageBackground
        style={styles.gaudiBackground}
        source={{uri:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQk3LUuAtkUjopKYyx2f9hfqTJXN9jJGUaAmQ&usqp=CAU"}}
    >
      <View style={styles.gaudiSlide}>
            <Text style={styles.titleText}>Antoni Gauidi's Famous Works</Text>
            <Text style={styles.text}>I'm Pedro Guerra from Chicago Illinois and I have a passion
              for using my abilities to help those that are searching for the 
              clarity that they need in their life.</Text>
        </View>
    </ImageBackground>
        <View style={styles.slide2}>
            <Text style={styles.titleText}>Sagrada Familia</Text>
            <Text style={styles.text}>Gaudí-designed landmark church</Text>
        </View>
        <View style={styles.slide3}>
            <Text style={styles.titleText}>Casa Batllo + La Pedrera</Text>
            <Text style={styles.text}>A Fantastical Gaudi-designed apartment building and masterpiece housing arts venue </Text>
        </View>
        <View style={styles.slide4}>
            <Text style={styles.titleText}>Parc Guell</Text>
            <Text style={styles.text}>Park with Gaudi's sculptural buildings</Text>
        </View>

      </Swiper>
      </View>
  </>
  )
}

export default InfoSlider

const styles = StyleSheet.create({
    sliderContainer: {
        elevation: 3.3,
        backgroundColor: "fff",
        height: 350,
        borderRadius: 46,
        flex: 1,
        marginTop: 50,
        margin: 28,
        justifyContent: 'center',
        alignItems: "center",
        textAlign: "center",
      },
      background1: {
        opacity: .8,
        height: 220,
        marginTop: 20,
        justifyContent: 'center',
        alignItems: "center",
        borderRadius: 20,
      },
      background2: {
        opacity: .8,
        height: 250,
        marginTop: 20,
        justifyContent: 'center',
        alignItems: "center",
        borderRadius: 20,
      },
      background3: {
        opacity: .8,
        height: 180,
        marginTop: 20,
        justifyContent: 'center',
        alignItems: "center",
        borderRadius: 20,
      },
      background4: {
        opacity: .8,
        height: 200,
        marginTop: 20,
        justifyContent: 'center',
        alignItems: "center",
        borderRadius: 20,
      },
      wrapper: {
        opacity: .9,
        height: 400,
        marginTop: 20,
        justifyContent: 'center',
        alignItems: "center",
        borderRadius: 20,
        
      },
      gaudiBackground: {
        blurRadius: 15,
        opacity: 1,
        flex: 1,
        resizeMode: "contain",
        width: width,
        height: 300,
        borderRadius: 40
      },
      gaudiSlide:{
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        },
      slide2:{
          flex: 1,
          justifyContent: "center",
          alignItems: "center",
        },
      slide3:{
          flex: 1,
          justifyContent: "center",
          alignItems: "center",
        },
      slide4:{
          flex: 1,
          justifyContent: "center",
          alignItems: "center",
        },
      titleText: {
        color: "black",
        fontSize: 30,
        fontWeight: "bold",
        textAlign: "center",
        },
      text: {
          color: "black",
          fontSize: 20,
          fontWeight: "bold",
          textAlign: "center",
        },

})
