import { Text, View, StyleSheet, ScrollView, ImageBackground, Pressable, Dimensions } from 'react-native'
import InfoSlider from "../Components/InfoSlider"
import PlacesSlider from "../Components/PlacesSlider"
import GaudiList from "../Modals/GaudiList"
import GaudiData from "../GaudiData"
import { useState } from "react"


const width = Dimensions.get("window").width
const height = Dimensions.get("window").height


function GaudiScreen({navigation}) {
 const [gaudiInfo, setGaudiInfo] = useState(GaudiData)
  return (
    <>
        <ScrollView> 
        <ImageBackground
            style={styles.background}
            source={{uri:("https://static.wixstatic.com/media/8fd44c_24ccddb3a0e84c0b80223fbfb586866b~mv2.jpg/v1/fill/w_902,h_600,al_c,q_90/8fd44c_24ccddb3a0e84c0b80223fbfb586866b~mv2.webp")}}
        >
        </ImageBackground>
            <ScrollView horizontal={true} style={{flex: 1, opacity: .5}} >
                    {/* <Pressable onPress={() => navigation.navigate("GaudiScreen")}> */}
                        <InfoSlider />
                    {/* </Pressable> */}
                    <PlacesSlider />
                    <PlacesSlider />
            </ScrollView>
                    <GaudiList 
                        gaudiInfo={gaudiInfo}
                    />
        </ScrollView>
</>
  )
}

export default GaudiScreen

const styles = StyleSheet.create({
    background: {
        opacity: 1,
        flex: 1,
        resizeMode: "contain",
        width: width,
        height: 310
    },
    
    
})
